import React from 'react';
import TacoRecipe from './TacoRecipe';
import { connect } from 'react-redux';
import fetch_recipe, { fetchRecipe } from '../actions/index'

const Tacos = (props) => {

    return(
        <>
        {props.recipes.map((item) => (<TacoRecipe recipe={item}/>))}
        <button onClick={() => fetchRecipe({ type: FETCH_RECIPE })}> Get new recipe </button>
        </>
    )
}

function mapStateToProps(state) {
    return {
        recipes: state.recipes,
    }
}

export default connect(mapStateToProps, {})(Tacos);
